import java.util.Scanner; public class Main { public static void main(String[] args) { Scanner scanner = new Scanner(System.in); String str = scanner.next(); int n = scanner.nextInt(); int x = 0; if (n == 1) { System.out.println(str); x = 1; } if (x == 0) { int m = str.length(); char[] a = new char[m]; int i = 0, j = 0, z = 0; for (int k = 0; k < m; k++) { a[k] = (char) i; if (z == 0) { i++; } else if (z == 1) { i--; } if (i == n) { i = n - 2; z = 1; } if (i == -1) { i = 1; z = 0; } } for (i = 0; i < n; i++) { for (j = 0; j < m; j++) { if (a[j] == i) System.out.print(str.charAt(j)); } } System.out.println(); } } }